function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    g_form.addErrorMessage(newValue);
    var ga = new GlideAjax('global.globalWorkoutAJAXUtil'); //calling script include
    ga.addParam('sysparm_name', 'fetchExercisesString'); //Script include function
    //ga.addParam('sysparm_routineId', newValue);
    ga.getXML(setExerciseDet);

