sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    g_form.addInfoMessage('Changing the start date');
    try {
        var addtime;
        var addtype = 'day';

        addtime = 7; //The amount of time to add


        var ajax = new GlideAjax('global.globalWorkoutAJAXUtil');
        ajax.addParam('sysparm_name', 'fetchExercisesString');

    //     ajax.addParam('sysparm_fdt', g_form.getValue('start_date'));
    // ajax.addParam('sysparm_addtime', addtime);
    // ajax.addParam('sysparm_addtype', addtype);
       ajax.getXML(doSomething); //Need callback function


    } catch (err) {
        g_form.addErrorMessage('Error caught' + err);

    }
}

function doSomething(response) {
    //Sets field
    var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.addInfoMessage('short_description' + answer);
    g_form.setValue('short_description', answer);

}
