sys_script_include.script:
var WorkOutMasterUTILAJAX = Class.create();
WorkOutMasterUTILAJAX.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    fetchExercises: function() {
        var dataArr = [];
        var exerciseRep = {};
        var routineId = this.getParameter('sysparm_routineId');

        if (routineId) {
			return;
        }
        var gr = new GlideRecord('x_1533383_workout_routine');
        gr.get(routineId);

        try {
            var exerciseArray = gr.getValue('exercise_list').split(',');
          //  g_form.getInfoMessage(exerciseArray);
            for (var i = 0; i < exerciseArray.length; i++) {

                exerciseRep = {
                    exercise: this.exerciseArray[i]
                };
           //     g_form.getInfoMessage(this.exerciseArray[i]);
                dataArr.push(exerciseRep);
            }
        } catch (err) {
            g_form.getInfoMessage(err);
        }
        return JSON.stringify(dataArr);

    },
	fetchExercisesString: function() {

        var routineId = this.getParameter('sysparm_routineId');
        if (routineId) {
			return;
        }
        var gr = new GlideRecord("x_1533383_workout_routine");
        gr.get(routineId);

		var exerciseList = 'd54a555883391a1038eaf855eeaad330,008a995883391a1038eaf855eeaad325,817c995883391a1038eaf855eeaad328,d39dc09783b1561038eaf855eeaad328'; // gr.getValue('exercise_list');

		return exerciseList;
	},
    type: 'WorkOutMasterUTILAJAX'
});
