sys_script_include.script:
var WorkOutMasterUTIL = Class.create();
WorkOutMasterUTIL.prototype = {
    initialize: function() {
        this.strengthSets = gs.getProperty('x_1533383_workout.workout.master.strength.sets');
    },
    checkOpenworkouts: function(programId) {
        if (gs.nil(programId)) {
            return;
        }
return programId;



    },
    fetchExercisesString: function(routineId) {
        if (routineId) {
            return;
        }
        var getExercise = new GlideRecord('x_1533383_workout_routine');
        getExercise.get(routineId);

        var exerciseList = getExercise.getValue('sys_id');

        return exerciseList.toString();
    },
    //x_1533383_workout.workout.master.strength.sets gs.
    createWorkoutReps: function(curr, routineID) {
        if (gs.nil(routineID)) {
            return;
        }
        var getWorkout = new GlideRecord('x_1533383_workout_routine');
        getWorkout.get(routineID);

        var exerciseArray = curr.getValue('exercise_list').split(',');
        for (var i = 1; i <= exerciseArray.length; i++) {

            exerciseRep = {
                exercise: exerciseArray[i],
                workout: curr.getValue('workout'),
                person: curr.getValue('person'),
                sets: this.strengthSets,
                order: i
            };
            this._createExerciseRep(JSON.stringify(exerciseRep));
        }
    },
    /**
       	* Description: 
       	* Parameters: 
    	      curr 
    	      workoutId: sys_id of current workout record
       	* Returns:
     */
    processWorkoutRPCreate: function(curr, workoutId, theLoop) {
        // gs.addErrorMessage(curr.variables.create_exercise_reps_on_new_workout.getRowCount());
        var variables = curr.variables.getElements();
        for (var i = 0; i < variables.length; i++) {
            var question = variables[i].getQuestion();
            gs.log(question.getLabel() + ":" + question.getValue())
        }
        var exerId = '618fbac18375de1038eaf855eeaad35a';
        var order = 1;
        var woOrder = 1;
        for (var i = 0; i < theLoop; i++) {
            var saveRep = new GlideRecord('x_1533383_workout_exercise_rep');
            saveRep.initialize();
            saveRep.setValue('exercise', exerId);
            saveRep.setValue('workout', workoutId);
            saveRep.setValue('order', order);
            saveRep.insert();
        }

    },
    /**
      	* Description: 

    	* Parameters: 

    	* Returns:
    	*/
    _createExerciseRep: function(gr) {
        if (gs.nil(gr)) {
            return;
        }
        gs.addInfoMessage('Calling the sub function');
        for (var setLoop = 1; setLoop <= gr.sets; setLoop++) {

            var saveRep = new GlideRecord('x_1533383_workout_exercise_rep');

            saveRep.initialize();
            saveRep.setValue('exercise', gr.exercise);
            saveRep.setValue('workout', gr.workout);
            saveRep.setValue('order', gr.order);
            saveRep.setValue('wo_order', gr.wo_order);

            saveRep.insert();
        }


    },
    type: 'WorkOutMasterUTIL'
};
